import React, { useState, useEffect } from 'react';
import WebSocketClient from 'websocket';

const LatencyDisplay = () => {
  const [latency, setLatency] = useState(null);

  useEffect(() => {
    const wsClient = new WebSocketClient.client();
    
    wsClient.connect('ws://localhost:5545');

    wsClient.on('connect', connection => {
      console.log('Connected to Pylon WebSocket server');

      connection.on('message', message => {
        if (message.type === 'utf8') {
          const packetTimestamp = parseInt(message.utf8Data);
          const currentTimestamp = Date.now();
          const packetLatency = currentTimestamp - packetTimestamp;

          setLatency(packetLatency);
        }
      });

      connection.on('close', () => {
        console.log('Connection closed');
      });
    });

    return () => {
      wsClient.abort();
    };
  }, []);

  return (
    <div>
      <h2>Packet Latency:</h2>
      {latency !== null ? (
        <p>{latency} ms</p>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default LatencyDisplay;
