import React, { Component } from 'react';
import { client as WebSocketClient } from 'websocket';

class LatencyDisplay extends Component {
  constructor(props) {
    super(props);

    this.state = {
      latency: null,
    };

    this.wsClient = new WebSocketClient();
  }

  componentDidMount() {
    this.wsClient.connect('ws://localhost:55455');

    this.wsClient.on('connect', connection => {
      console.log('Connected to Pylon WebSocket server');

      connection.on('message', message => {
        if (message.type === 'utf8') {
          const packetTimestamp = parseInt(message.utf8Data);
          const currentTimestamp = Date.now();
          const packetLatency = currentTimestamp - packetTimestamp;

          this.setState({
            latency: packetLatency,
          });
        }
      });

      connection.on('close', () => {
        console.log('Connection closed');
      });
    });
  }

  componentWillUnmount() {
    this.wsClient.abort();
  }

  render() {
    const { latency } = this.state;

    return (
      <div>
        <h2>Packet Latency:</h2>
        {latency !== null ? (
          <p>{latency} ms</p>
        ) : (
          <p>Loading...</p>
        )}
      </div>
    );
  }
}

export default LatencyDisplay;
